class Logic{
  constructor( Map ){
    this.Map = Map;
  }
  logic(){
    this.Map.tiles.forEach( ( row, ri ) => {
      row.forEach( ( Tile, ti ) => {
        Tile.logic();
      } )
    } );

    this.Map.objects.forEach( ( Object, oi ) => {
      Object.logic();
    } );
  }
}
module.exports = { Logic: Logic };
