class Renderer{
  constructor( Map, context ){
    this.Map = Map;
    this.ctx = context;
  }
  render(){
    this.Map.tiles.forEach( ( row, ri ) => {
      row.forEach( ( Tile, ti ) => {
        Tile.draw( this.ctx );
      } )
    } );

    this.Map.objects.forEach( ( Object, oi ) => {
      Object.draw( this.ctx );
    } );
    
    PlayerTeam.selection.forEach( ( Object, oi ) => {
      Object.drawSelection( this.ctx );
    } )
  }
}
module.exports = { Renderer: Renderer };
