const fs = require('fs');

class UIComponent{
  constructor( name, path ){
    this.element = document.createElement( "section" );
    this.element.id = name;
    this.element.className = `ui-component ${name}`;
    this.element.innerHTML = fs.readFileSync( path, "UTF-8" );
  }
}
module.exports = { UIComponent: UIComponent };
